import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [location, setLocation] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");
  const [isDarkMode, setIsDarkMode] = useState(true);
  const Api_key = "6ae780ec702315938759f44d1011278c";

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const fetchWeather = async () => {
    if (!location) {
      setError("Please enter a location");
      return;
    }

    setError("");
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&appid=${Api_key}`
      );
      setWeather(response.data);
    } catch (err) {
      setError("Could not fetch weather data. Check your location.");
    }
  };

  return (
    <div style={{
      backgroundColor: isDarkMode ? "#1E1E1E" : "#f5f5f5",
      minHeight: "100vh",
      color: isDarkMode ? "white" : "#333",
      fontFamily: "'Segoe UI', Arial, sans-serif",
      padding: "20px",
      transition: "all 0.3s ease"
    }}>
      {/* Header */}
      <header style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "20px"
      }}>
        <div style={{ display: "flex", alignItems: "center", gap: "20px" }}>
          <button style={{ background: "none", border: "none", color: "white" }}>☰</button>
          <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
            <span>📍</span>
            <span>{weather ? weather.city.name : "Location"}</span>
          </div>
        </div>
        <div style={{
          display: "flex",
          alignItems: "center",
          gap: "20px",
          backgroundColor: "#2A2A2A",
          padding: "10px 20px",
          borderRadius: "20px",
          width: "400px"
        }}>
          <span>🔍</span>
          <input
            style={{
              background: "none",
              border: "none",
              color: "white",
              width: "100%",
              fontSize: "16px"
            }}
            type="text"
            placeholder="Search city..."
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && fetchWeather()}
          />
        </div>
        <div style={{ display: "flex", gap: "20px" }}>
          <button 
            onClick={toggleTheme}
            style={{ 
              background: "none", 
              border: "none", 
              color: isDarkMode ? "white" : "#333",
              fontSize: "24px"
            }}
          >
            {isDarkMode ? "🌙" : "☀️"}
          </button>
        </div>
      </header>

      {/* Main Content */}
      <main style={{ padding: "20px" }}>
        <div style={{ display: "flex", gap: "20px", marginBottom: "20px" }}>
          <button style={{
            background: "#3B82F6",
            border: "none",
            padding: "8px 20px",
            borderRadius: "20px",
            color: "white"
          }}>Forecast</button>
          <button style={{
            background: "none",
            border: "1px solid #3B82F6",
            padding: "8px 20px",
            borderRadius: "20px",
            color: "white"
          }}>Air quality</button>
        </div>

        {weather && (
          <div style={{
            display: "grid",
            gridTemplateColumns: "1fr 2fr",
            gap: "20px"
          }}>
            {/* Current Weather */}
            <div style={{
              backgroundColor: "#2A2A2A",
              borderRadius: "20px",
              padding: "20px"
            }}>
              <div style={{ fontSize: "64px", fontWeight: "bold" }}>
                {Math.round(weather.list[0].main.temp)}°
              </div>
              <div style={{ opacity: 0.7 }}>
                Real feel {Math.round(weather.list[0].main.feels_like)}°
              </div>
              <div style={{ marginTop: "20px" }}>
                <div>Wind: {weather.list[0].wind.speed} m/s</div>
                <div>Humidity: {weather.list[0].main.humidity}%</div>
                <div>Pressure: {weather.list[0].main.pressure}MB</div>
              </div>
            </div>

            {/* Weekly Forecast */}
            <div style={{
              backgroundColor: "#2A2A2A",
              borderRadius: "20px",
              padding: "20px",
              display: "grid",
              gridTemplateColumns: "repeat(7, 1fr)",
              gap: "10px"
            }}>
              {weather.list.slice(0, 7).map((day, index) => (
                <div key={index} style={{ textAlign: "center" }}>
                  <div>{new Date(day.dt * 1000).toLocaleDateString('en-US', { weekday: 'short' })}</div>
                  <img 
                    src={`https://openweathermap.org/img/wn/${day.weather[0].icon}@2x.png`}
                    alt={day.weather[0].description}
                    style={{ width: "50px", height: "50px" }}
                  />
                  <div>{Math.round(day.main.temp)}°</div>
                </div>
              ))}
            </div>
          </div>
        )}
        {/* India Map Section */}
        <div style={{
          backgroundColor: isDarkMode ? "#2A2A2A" : "#ffffff",
          borderRadius: "20px",
          padding: "20px",
          marginTop: "20px"
        }}>
          <h3 style={{ marginBottom: "20px" }}>Weather Map - India</h3>
          <div style={{
            position: "relative",
            width: "100%",
            height: "400px",
            backgroundColor: isDarkMode ? "#1E1E1E" : "#e0e0e0",
            borderRadius: "15px",
            overflow: "hidden"
          }}>
            <img 
              src="https://www.mapsofindia.com/maps/india/india-political-map.jpg"
              alt="India Map"
              style={{
                width: "100%",
                height: "100%",
                objectFit: "contain",
                opacity: isDarkMode ? 0.7 : 1
              }}
            />
            {weather && (
              <div style={{
                position: "absolute",
                top: "50%",
                left: "50%",
                transform: "translate(-50%, -50%)",
                backgroundColor: isDarkMode ? "rgba(0,0,0,0.7)" : "rgba(255,255,255,0.7)",
                padding: "10px",
                borderRadius: "10px"
              }}>
                <p>{weather.name}</p>
                <p>{Math.round(weather.main.temp)}°C</p>
              </div>
            )}
          </div>
        </div>

        {/* Other Cities */}
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
          gap: "20px",
          marginTop: "20px"
        }}>
          {["Mumbai", "Delhi", "Bangalore", "Chennai"].map((city) => (
            <div key={city} style={{
              backgroundColor: isDarkMode ? "#2A2A2A" : "#ffffff",
              borderRadius: "15px",
              padding: "15px",
              cursor: "pointer"
            }} onClick={() => {
              setLocation(city);
              fetchWeather();
            }}>
              <h4>{city}</h4>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default WeatherApp;
