import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [location, setLocation] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");
  const Api_key = "6ae780ec702315938759f44d1011278c";

  const fetchWeather = async () => {
    if (!location) {
      setError("Please enter a location");
      return;
    }

    setError("");
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=${Api_key}`
      );
      setWeather(response.data);
    } catch (err) {
      setError("Could not fetch weather data. Check your location.");
    }
  };

  const convertTimestamp = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleTimeString();
  };

  return (
    <div style={{ 
      padding: "40px",
      backgroundColor: "#1a1a1a",
      minHeight: "100vh",
      color: "white",
      fontFamily: "'Segoe UI', Arial, sans-serif"
    }}>
      <div style={{
        maxWidth: "1200px",
        margin: "0 auto",
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
        gap: "20px"
      }}>
        {/* Search Card */}
        <div style={{
          background: "rgba(255, 255, 255, 0.1)",
          borderRadius: "20px",
          padding: "20px",
          backdropFilter: "blur(10px)"
        }}>
          <input
            style={{
              width: "100%",
              padding: "12px",
              borderRadius: "10px",
              border: "none",
              backgroundColor: "rgba(255, 255, 255, 0.1)",
              color: "white",
              fontSize: "16px",
              marginBottom: "10px"
            }}
            type="text"
            placeholder="Search location..."
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <button 
            onClick={fetchWeather}
            style={{
              width: "100%",
              padding: "12px",
              borderRadius: "10px",
              border: "none",
              backgroundColor: "#4CAF50",
              color: "white",
              fontSize: "16px",
              cursor: "pointer"
            }}
          >
            Get Weather
          </button>
        </div>

        {error && (
          <div style={{
            background: "#ff5252",
            borderRadius: "20px",
            padding: "20px",
            color: "white"
          }}>
            {error}
          </div>
        )}

        {weather && (
          <>
            {/* Main Weather Card */}
            <div style={{
              background: "rgba(135, 206, 235, 0.2)",
              borderRadius: "20px",
              padding: "30px",
              textAlign: "center"
            }}>
              <h2 style={{ fontSize: "24px", marginBottom: "20px" }}>
                {weather.name}, {weather.sys.country}
              </h2>
              <div style={{ fontSize: "48px", fontWeight: "bold" }}>
                {Math.round(weather.main.temp)}°C
              </div>
              <img 
                src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
                alt={weather.weather[0].description}
                style={{ width: "100px", height: "100px" }}
              />
              <p style={{ 
                textTransform: 'capitalize',
                fontSize: "20px"
              }}>
                {weather.weather[0].description}
              </p>
            </div>

            {/* Details Card */}
            <div style={{
              background: "rgba(255, 255, 255, 0.1)",
              borderRadius: "20px",
              padding: "20px"
            }}>
              <div style={{ marginBottom: "20px" }}>
                <h3 style={{ color: "#4CAF50", marginBottom: "10px" }}>Details</h3>
                <p>Feels Like: {Math.round(weather.main.feels_like)}°C</p>
                <p>Humidity: {weather.main.humidity}%</p>
                <p>Wind: {weather.wind.speed} m/s</p>
                <p>Pressure: {weather.main.pressure} hPa</p>
              </div>
              
              <div>
                <h3 style={{ color: "#4CAF50", marginBottom: "10px" }}>Sun Schedule</h3>
                <p>Sunrise: {convertTimestamp(weather.sys.sunrise)}</p>
                <p>Sunset: {convertTimestamp(weather.sys.sunset)}</p>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default WeatherApp;
