import React, { useState } from "react";
import axios from "axios";


const WeatherApp = () => {
  const [location, setLocation] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const fetchWeather = async () => {
    if (!location) {
      setError("Please enter a location");
      return;
    }

    setError("");
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?lat=44.34&lon=10.99&appid={API key}`
      );
      setWeather(response.data);
    } catch (err) {
      setError("Could not fetch weather data. Check your location.");
    }
  };

  const convertTimestamp = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleTimeString();
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Weather App</h2>
      <input
        type="text"
        placeholder="Enter Location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button onClick={fetchWeather}>Get Weather</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {weather && (
        <div style={{ maxWidth: "600px", margin: "0 auto", padding: "20px" }}>
          <h3>Weather in {weather.name}, {weather.sys.country}</h3>
          
          <div style={{ margin: "20px 0" }}>
            <img 
              src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
              alt={weather.weather[0].description}
            />
            <p style={{ textTransform: 'capitalize' }}>
              {weather.weather[0].main} - {weather.weather[0].description}
            </p>
          </div>
          
          <div style={{ display: 'grid', gap: '15px', textAlign: 'left' }}>
            <div>
              <h4>Temperature</h4>
              <p>Current: {Math.round(weather.main.temp)}°C</p>
              <p>Feels Like: {Math.round(weather.main.feels_like)}°C</p>
              <p>Min/Max: {Math.round(weather.main.temp_min)}°C / {Math.round(weather.main.temp_max)}°C</p>
            </div>

            <div>
              <h4>Atmospheric Conditions</h4>
              <p>Pressure: {weather.main.pressure} hPa</p>
              <p>Sea Level: {weather.main.sea_level || weather.main.pressure} hPa</p>
              <p>Ground Level: {weather.main.grnd_level || 'N/A'} hPa</p>
              <p>Humidity: {weather.main.humidity}%</p>
            </div>

            <div>
              <h4>Wind</h4>
              <p>Speed: {weather.wind.speed} m/s</p>
              <p>Direction: {weather.wind.deg}°</p>
              <p>Gust: {weather.wind.gust || 'N/A'} m/s</p>
            </div>

            <div>
              <h4>Other Information</h4>
              <p>Visibility: {(weather.visibility / 1000).toFixed(1)} km</p>
              <p>Cloudiness: {weather.clouds.all}%</p>
              {weather.rain && <p>Rain (1h): {weather.rain["1h"]} mm</p>}
              {weather.snow && <p>Snow (1h): {weather.snow["1h"]} mm</p>}
            </div>

            <div>
              <h4>Sun Schedule</h4>
              <p>Sunrise: {convertTimestamp(weather.sys.sunrise)}</p>
              <p>Sunset: {convertTimestamp(weather.sys.sunset)}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WeatherApp;
