import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [location, setLocation] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const fetchWeather = async () => {
    if (!location) {
      setError("Please enter a location");
      return;
    }

    setError("");
    try {
      // First, get coordinates for the location
      const geoResponse = await axios.get(
        `http://api.openweathermap.org/geo/1.0/direct?q=${location}&limit=1&appid=YOUR_API_KEY`
      );

      if (geoResponse.data.length === 0) {
        setError("Location not found");
        return;
      }

      const { lat, lon } = geoResponse.data[0];

      // Then get weather data using coordinates
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=metric&appid=YOUR_API_KEY`
      );
      setWeather(response.data);
    } catch (err) {
      setError("Could not fetch weather data. Check your location.");
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Weather App</h2>
      <input
        type="text"
        placeholder="Enter Location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button onClick={fetchWeather}>Get Weather</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {weather && (
        <div>
          <h3>Weather in {weather.name}</h3>
          <p>Temperature: {Math.round(weather.main.temp)}°C</p>
          <p>Feels like: {Math.round(weather.main.feels_like)}°C</p>
          <p>Condition: {weather.weather[0].description}</p>
          <p>Humidity: {weather.main.humidity}%</p>
          <p>Wind Speed: {weather.wind.speed} m/s</p>
          <img 
            src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
            alt="weather icon"
          />
        </div>
      )}
    </div>
  );
};

export default WeatherApp;
