import React, { useState } from "react";
import axios from "axios";

const WeatherApp = () => {
  const [location, setLocation] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");
  const [isDarkMode, setIsDarkMode] = useState(true);
  const Api_key = "6ae780ec702315938759f44d1011278c";

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const fetchWeather = async () => {
    if (!location) {
      setError("Please enter a location");
      return;
    }

    setError("");
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&appid=${Api_key}`
      );
      setWeather(response.data);
    } catch (err) {
      setError("Could not fetch weather data. Check your location.");
    }
  };

  return (
    <div style={{
      background: isDarkMode 
        ? "linear-gradient(135deg, #1a1f35 0%, #0F172A 100%)"
        : "linear-gradient(135deg, #e0f2fe 0%, #bfdbfe 100%)",
      minHeight: "100vh",
      color: isDarkMode ? "white" : "#1E293B",
      fontFamily: "'Segoe UI', Arial, sans-serif",
      padding: "20px",
      transition: "all 0.3s ease",
      position: "relative",
      overflow: "hidden"
    }}>
      {/* Animated Background Elements */}
      <div style={{
        position: "absolute",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        overflow: "hidden",
        zIndex: 0,
        opacity: 0.5
      }}>
        {[...Array(5)].map((_, i) => (
          <div key={i} style={{
            position: "absolute",
            width: "300px",
            height: "300px",
            background: isDarkMode 
              ? "radial-gradient(circle, rgba(59, 130, 246, 0.1) 0%, rgba(59, 130, 246, 0) 70%)"
              : "radial-gradient(circle, rgba(59, 130, 246, 0.05) 0%, rgba(59, 130, 246, 0) 70%)",
            borderRadius: "50%",
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            transform: "translate(-50%, -50%)",
            animation: "float 20s infinite",
            animationDelay: `${i * 2}s`
          }} />
        ))}
      </div>

      {/* Main Content Container */}
      <div style={{
        position: "relative",
        zIndex: 1,
        maxWidth: "1400px",
        margin: "0 auto",
        backdropFilter: "blur(10px)"
      }}>
        {/* Header with enhanced glass effect */}
        <header style={{
          background: isDarkMode 
            ? "rgba(30, 41, 59, 0.8)"
            : "rgba(255, 255, 255, 0.8)",
          backdropFilter: "blur(20px)",
          borderRadius: "30px",
          padding: "20px 30px",
          boxShadow: isDarkMode 
            ? "0 8px 32px rgba(0, 0, 0, 0.3)"
            : "0 8px 32px rgba(100, 100, 255, 0.1)",
          border: isDarkMode 
            ? "1px solid rgba(255, 255, 255, 0.1)"
            : "1px solid rgba(255, 255, 255, 0.5)",
          marginBottom: "30px"
        }}>
          <div style={{ display: "flex", alignItems: "center", gap: "20px" }}>
            <button style={{ background: "none", border: "none", color: isDarkMode ? "white" : "#333" }}>‚ò∞</button>
            <div style={{ display: "flex", alignItems: "center", gap: "10px", color: isDarkMode ? "white" : "#333" }}>
              <span>üìç</span>
              <span>{weather ? weather.city.name : "Location"}</span>
            </div>
          </div>
          <div style={{
            display: "flex",
            alignItems: "center",
            gap: "20px",
            backgroundColor: isDarkMode ? "#2A2A2A" : "#f0f0f0",
            padding: "10px 20px",
            borderRadius: "20px",
            width: "400px"
          }}>
            <span>üîç</span>
            <input
              style={{
                background: "none",
                border: "none",
                color: isDarkMode ? "white" : "#333",
                width: "100%",
                fontSize: "16px"
              }}
              type="text"
              placeholder="Search city..."
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && fetchWeather()}
            />
          </div>
          <div style={{ display: "flex", gap: "20px" }}>
            <button 
              onClick={toggleTheme}
              style={{ 
                background: "none", 
                border: "none", 
                color: isDarkMode ? "white" : "#333",
                fontSize: "24px"
              }}
            >
              {isDarkMode ? "üåô" : "‚òÄÔ∏è"}
            </button>
          </div>
        </header>

        {/* Main Weather Content */}
        <main style={{ 
          display: "grid",
          gap: "30px",
          gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))"
        }}>
          {weather && (
            <>
              {/* Current Weather Card */}
              <div style={{
                background: isDarkMode 
                  ? "linear-gradient(135deg, rgba(42, 67, 101, 0.9), rgba(30, 41, 59, 0.9))"
                  : "linear-gradient(135deg, rgba(255, 255, 255, 0.9), rgba(248, 250, 252, 0.9))",
                backdropFilter: "blur(20px)",
                borderRadius: "30px",
                padding: "40px",
                boxShadow: isDarkMode 
                  ? "0 8px 32px rgba(0, 0, 0, 0.3)"
                  : "0 8px 32px rgba(100, 100, 255, 0.1)",
                border: isDarkMode 
                  ? "1px solid rgba(255, 255, 255, 0.1)"
                  : "1px solid rgba(255, 255, 255, 0.5)",
                transform: "translateY(0)",
                transition: "all 0.3s ease",
                cursor: "pointer",
                "&:hover": {
                  transform: "translateY(-10px)",
                  boxShadow: isDarkMode 
                    ? "0 12px 40px rgba(0, 0, 0, 0.4)"
                    : "0 12px 40px rgba(100, 100, 255, 0.2)"
                }
              }}>
                <div style={{ 
                  fontSize: "72px", 
                  fontWeight: "bold",
                  background: isDarkMode 
                    ? "linear-gradient(135deg, #60A5FA, #3B82F6)"
                    : "linear-gradient(135deg, #3B82F6, #2563EB)",
                  WebkitBackgroundClip: "text",
                  WebkitTextFillColor: "transparent"
                }}>
                  {Math.round(weather.list[0].main.temp)}¬∞
                </div>
                <div style={{ opacity: isDarkMode ? 0.7 : 0.8 }}>
                  Real feel {Math.round(weather.list[0].main.feels_like)}¬∞
                </div>
                <div style={{ marginTop: "20px" }}>
                  <div>Wind: {weather.list[0].wind.speed} m/s</div>
                  <div>Humidity: {weather.list[0].main.humidity}%</div>
                  <div>Pressure: {weather.list[0].main.pressure}MB</div>
                </div>
              </div>

              {/* Forecast Section */}
              <div style={{
                background: isDarkMode 
                  ? "linear-gradient(135deg, rgba(30, 41, 59, 0.9), rgba(15, 23, 42, 0.9))"
                  : "linear-gradient(135deg, rgba(255, 255, 255, 0.9), rgba(240, 249, 255, 0.9))",
                backdropFilter: "blur(20px)",
                borderRadius: "30px",
                padding: "40px",
                boxShadow: isDarkMode 
                  ? "0 8px 32px rgba(0, 0, 0, 0.3)"
                  : "0 8px 32px rgba(100, 100, 255, 0.1)",
                border: isDarkMode 
                  ? "1px solid rgba(255, 255, 255, 0.1)"
                  : "1px solid rgba(255, 255, 255, 0.5)"
              }}>
                {/* ... existing forecast content with enhanced styling ... */}
              </div>
            </>
          )}
        </main>
      </div>

      {/* Add this to your CSS */}
      <style>
        {`
          @keyframes float {
            0%, 100% { transform: translate(-50%, -50%) translateY(0px); }
            50% { transform: translate(-50%, -50%) translateY(-20px); }
          }
        `}
      </style>
    </div>
  );
};

export default WeatherApp;
